export default {
  name: 'CellularAutomaton',
  props: {
    heroHt: Number
  },
  data() {
    return {
      cellSize: 10,
      timeCount: 0,
      timeInterval: 100,
    }
  },
  computed: {
    cellsPerRow() {
      return Math.ceil(window.innerWidth / this.cellSize)
    },
    rows() {
      return Math.ceil(this.heroHt / this.cellSize)
    }
  },
  methods: {
    arraysEqual(a, b) {
      if (a === b) return true
      if (a == null || b == null) return false
      if (a.length != b.length) return false

      for (let i = 0; i < a.length; i++) {
        if (a[i] !== b[i]) return false
      }
      return true
    }
  },
  mounted() {
    const nlToArr = el => Array.prototype.slice.call(el)
    nlToArr(this.$refs.cellRow[0].childNodes).map(x => {
      let rand = Math.round(Math.random())
      x.classList.add(rand === 0 ? 'false' : 'true')
    })
    for (let i = 1; i < this.rows; i++) {
      for (let j = 0; j < this.cellsPerRow; j++) {
        let pattern = []
        if (j > 0 && j < this.cellsPerRow - 1) {
          pattern.push(this.$refs.cellRow[i - 1].childNodes[j - 1].className)
          pattern.push(this.$refs.cellRow[i - 1].childNodes[j].className)
          pattern.push(this.$refs.cellRow[i - 1].childNodes[j + 1].className)
        } else if (j === 0) {
          pattern.push('false')
          pattern.push(this.$refs.cellRow[i - 1].childNodes[j].className)
          pattern.push(this.$refs.cellRow[i - 1].childNodes[j + 1].className)
        } else if (j === this.cellsPerRow - 1) {
          pattern.push(this.$refs.cellRow[i - 1].childNodes[j - 1].className)
          pattern.push(this.$refs.cellRow[i - 1].childNodes[j].className)
          pattern.push('false')
        }
        if (
          this.arraysEqual(pattern, ['true', 'true', 'true']) ||
          this.arraysEqual(pattern, ['true', 'false', 'false']) ||
          this.arraysEqual(pattern, ['false', 'true', 'false']) ||
          this.arraysEqual(pattern, ['false', 'false', 'true'])
        ) {
          this.$refs.cellRow[i].childNodes[j].classList.add('true')
        } else {
          this.$refs.cellRow[i].childNodes[j].classList.add('false')
        }
      }
    }
    for (let i = 0; i < this.rows; i++) {
      setTimeout(() => this.$refs.cellRow[i].classList.add('show'), this.timeCount)
      this.timeCount = this.timeCount + this.timeInterval
    }
  }
}
